One.onLoad(() =>
    class {
        static initRating() {
            jQuery(".js-rating").each((a, t) => {
                let e = jQuery(t);
                e.raty({
                    score: 0,
                    number: e.data("number") || 5,
                    cancel: e.data("cancel") || !1,
                    target: e.data("target") || !1,
                    targetScore: e.data("target-score") || !1,
                    precision: e.data("precision") || !1,
                    cancelOff:
                        e.data("cancel-off") ||
                        "fa fa-fw fa-times-circle text-danger",
                    cancelOn: e.data("cancel-on") || "fa fa-fw fa-times-circle",
                    starHalf:
                        e.data("star-half") ||
                        "fa fa-fw fa-star-half text-warning",
                    starOff:
                        e.data("star-off") || "fa fa-fw fa-star text-muted",
                    starOn:
                        e.data("star-on") || "fa fa-fw fa-star text-warning",
                    starType: "i",
                    hints: [
                        "Sangat Buruk!",
                        "Buruk!",
                        "Cukup!",
                        "Baik!",
                        "Sangat Baik!",
                    ],
                    click: function (score, event) {
                        // Update hint text with score
                        let target = jQuery(this).data("target");
                        if (target) {
                            jQuery(target).text(score);
                        }
                        // Set rating input value
                        jQuery("#rating").val(score);
                    },
                });
            });
        }
        static init() {
            this.initRating();
            // Handle form submit
            jQuery("#rating-form").submit(function (event) {
                event.preventDefault();
                let formData = jQuery(this).serialize();
                // Send form data to server
                jQuery.post("/save-rating", formData, function (response) {
                    alert(response.message);
                });
            });
        }
    }.init()
);


